document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".premium-countup__wrap")?.forEach(n=>{var e=n.querySelector(".premium-countup__increment"),t=Number(e.getAttribute("data-interval"))/1e3;let u=Number(e.getAttribute("data-delay"));var o=Number(e.textContent);if(!isNaN(o)){let r=new countUp.CountUp(e,o,{duration:t,useGrouping:!1,enableScrollSpy:!1,useEasing:!1});if(!r.error){let t=new IntersectionObserver(e=>{e.forEach(e=>{e.isIntersecting&&setTimeout(()=>{r.start(),t.unobserve(n)},u)})},{threshold:.1});t.observe(n)}}})});